import React, { useState , useContext, useEffect } from 'react';
import '../css/DashBoard.css';
import { UserContext } from "../App.js";

function DashBoard() {

  const globalState = useContext(UserContext); // 전역 상태

  const [withdrawMembers, setWithdrawMembers] = useState(null);  // 탈퇴회원목록
  const [deleteBoards, setDeleteBoards] = useState(null);  // 삭제게시글목록
  const [loading, setLoading] = useState(true); // 로딩 상태
  const [menu, setMenu] = useState(1); // 1:복구, 2: 통계, 3:관리자메뉴

  // 탈퇴한 회원 목록 조회용 비동기 요청 함수
  const getWithdrawnMemberList = () => {
    fetch('/admin/withdrawnMemberList')
    .then(response => response.json())
    .then(data => {
      
      if(data.length == 0) {
        setWithdrawMembers(null);
      } else {
        setWithdrawMembers(data);
      }
      
      setLoading(false); // 로딩 상태 변경
    })
    .catch(error => {
      console.error('Error fetching data:', error);
      setLoading(false); // 로딩 상태 변경
    });
  }

  // 탈퇴한 회원 복구 비동기 요청 함수
  const restoreMember = (e, member) => {
    console.log(e);
    console.log(member);

    if(window.confirm(member.memberNickname + "님을 탈퇴 복구 시키겠습니까?")) {
      fetch("/admin/restoreMember", {
        method : "PUT",
        headers : {"Content-Type" : "application/json"},
        body : JSON.stringify({ memberNo: member.memberNo })
      })
      .then(res => res.text())
      .then(result => {
        alert("복구 되었습니다");
        getWithdrawnMemberList();
      })
    } else{
      alert("취소");
    }
  }

  // 삭제된 게시글 목록 조회용 비동기 요청 함수
  const getDeleteBoardList = () => {
    fetch('/admin/deleteBoardList')
    .then(response => response.json())
    .then(data => {

      console.log("data: ", data);

      if(data.length == 0) {
        setDeleteBoards(null);
      } else {
        setDeleteBoards(data);
      }

      setLoading(false); // 로딩 상태 변경
    })
    .catch(error => {
      console.error('Error fetching data:', error);
      setLoading(false); // 로딩 상태 변경
    });
  }

  // 삭제된 게시글 복구 비동기 요청 함수
  const restoreBoard = (e, board) => {
    console.log(e);
    console.log(board);

    if(window.confirm(board.boardNo + "번 게시글을 복구 시키겠습니까?")) {
      fetch("/admin/restoreBoard", {
        method : "PUT",
        headers : {"Content-Type" : "application/json"},
        body : JSON.stringify({ boardNo: board.boardNo })
      })
      .then(res => res.text())
      .then(result => {
        alert("복구 되었습니다");
        getDeleteBoardList();
      })
    } else{
      alert("취소");
    }
  }

  // 컴포넌트가 렌더링 될 때마다 side effect를 실행
  useEffect(() => {
    getWithdrawnMemberList();
    getDeleteBoardList();
  }, []);

  

  return (
      <div className='dash-board-container'>
        <h1>관리자 페이지</h1>

        <div className='admin-info'>
          <p>현재 접속 관리자 : {globalState.user.memberNickname}</p>
          <button onClick={globalState.handleLogout}>로그아웃</button>
        </div>

        <ul className='tab-box'>
          <li>복구</li>
          <li>통계</li>
          <li>관리자 메뉴</li>
        </ul>
        
          

      </div>
  )
}

export default DashBoard;
