import React, { useState } from "react";
import Login from "./components/Login";
import DashBoard from "./components/DashBoard";

export const UserContext = React.createContext();

function App() {

  const [user, setUser] = useState({
    memberEmail: "",
    memberNickname : ""
  });
  //const [isLogin, setIsLogin] = useState(localStorage.getItem("userData"));
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // 이메일 입력 받아 상태에 세팅
  const changeInputEmail = (e) => {
    setEmail(e.target.value);
  }

  // 비밀번호 입력 받아 상태에 세팅
  const changeInputPw = (e) => {
    setPassword(e.target.value);
  }

  // submit 시 작동
  const handleSubmit = (e) => {
    // 기본 submit 이벤트 막기
    e.preventDefault();

    // login api 호출
    fetch("/admin/login", {
      method : "POST",
      headers : {"Content-Type" : "application/json"},
      body : JSON.stringify({
        memberEmail : email, 
        memberPw : password
      })
    })
    .then(res => res.json())
    .then(result => {
      console.log(result);

      // 저장할 데이터
      const data = {
        memberEmail: result.memberEmail,
        memberNickname: result.memberNickname
      };

      // 현재 시간을 milliseconds로 구함
      const currentTime = new Date().getTime();

      // 1시간 후의 시간을 milliseconds로 구함
      const expirationTime = currentTime + 60 * 60 * 1000; // 60분 * 60초 * 1000밀리초

      // 데이터와 만료 시간을 localStorage에 저장
      localStorage.setItem('userData', JSON.stringify(data));
      localStorage.setItem('expirationTime', expirationTime);

      // 만료 시간을 확인하기 위해 타이머 설정
      setTimeout(() => {
        // 만료 시간이 지나면 localStorage에서 데이터 삭제
        localStorage.removeItem('userData');
        localStorage.removeItem('expirationTime');
      }, 60); // 1시간 후

      setUser(data);
      //setIsLogin()

      });
  }

  // user라는 객체에 상태와, 함수를 묶어서 
  // Provider를 통해 하위 컴포넌트로 데이터 전달함
  const userData = {
    user : user,
    changeInputEmail : changeInputEmail,
    changeInputPw : changeInputPw,
    handleSubmit : handleSubmit
  }

  return (
    <div>
      <UserContext.Provider value={userData}>
        {
          localStorage.getItem("userData") != null?
            <DashBoard />
            :
            <Login />
          }
      </UserContext.Provider>
    </div>
  );
}

export default App;
