import React, { useState , useContext, useEffect } from 'react';
import '../css/DashBoard.css';
import { UserContext } from "../App.js";

function DashBoard() {

  const globalState = useContext(UserContext);

  const [withdrawMembers, setWithdrawMembers] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // 비동기 요청(fetch 등)을 보내고 데이터를 가져옴
    fetch('/admin/withdrawnMemberList')
      .then(response => response.json())
      .then(data => {

        console.log(data);
        // 데이터를 상태에 저장
        setWithdrawMembers(data);
        setLoading(false); // 로딩 상태 변경
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setLoading(false); // 로딩 상태 변경
      });
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행되도록 함

  const handleClick = (e, member) => {
    console.log(e);
    console.log(member);

    if(window.confirm(member.memberNickname + "님을 탈퇴 복구 시키겠습니까?")) {
      fetch("/admin/restoreMember", {
        method : "POST",
        headers : {"Content-Type" : "application/json"},
        body : member.memberNo
      })
    } else{
      alert("취소");
    }

    
  }

  return (
      <div className='dash-board-container'>
        <p>관리자 페이지</p>
        <p>현재 접속 관리자 : {globalState.user.memberNickname}</p>
        
          {
            loading ?
            <div>Loading...</div>
            :
            <div className='menu-box'>
              <section className='section-border'>
                <h3>탈퇴 회원 복구</h3>
                <div>
                  <p>탈퇴한 회원 목록</p>
                  {
                    withdrawMembers.map((member, index) => {
                      return (
                        <ul key={index}>
                          <li>회원 번호 : {member.memberNo}</li>
                          <li>회원 이메일 : {member.memberEmail}</li>
                          <li>회원 닉네임 : {member.memberNickname}</li>
                          <button onClick={(e) => handleClick(e, member)}>복구</button>
                        </ul>
                      )
                    })
                  }
                </div>
              </section>
    
              <section className='section-border'>  
                <h3>삭제 게시글 복구</h3>
              </section>
            </div>
          }
       

      </div>
  )
}

export default DashBoard;
