import { useState } from "react";
import Login from "./components/Login";
import DashBoard from "./components/DashBoard";
import { UserContext } from "./components/Login";

function App() {

  const [user, setUser] = useState({
    memberEmail: "",
    memberNickname : ""
  });
  const [isLogin, setIsLogin] = useState(localStorage.getItem("loginInfo"));
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // 이메일 입력 받아 상태에 세팅
  const changeInputEmail = (e) => {
    setEmail(e.target.value);
  }

  // 비밀번호 입력 받아 상태에 세팅
  const changeInputPw = (e) => {
    setPassword(e.target.value);
  }

  // submit 시 작동
  const handleSubmit = (e) => {
    // 기본 submit 이벤트 막기
    e.preventDefault();

    // login api 호출
    fetch("/admin/login", {
      method : "POST",
      headers : {"Content-Type" : "application/json"},
      body : JSON.stringify({
        memberEmail : email, 
        memberPw : password
      })
    })
    .then(res => res.json())
    .then(result => {
      console.log(result);
      setUser({ memberEmail : result.memberEmail, 
              memberNickname : result.memberNickname});

      const now = new Date();
      const item = {
        value : result,
        expiry : now.getTime()
      };

      localStorage.setItem("loginInfo", item);
      setIsLogin("true");

    });
  }

  // user라는 객체에 상태와, 함수를 묶어서 
  // Provider를 통해 하위 컴포넌트로 데이터 전달함
  const userData = {
    user : user,
    email : email,
    password : password, 
    changeInputEmail : changeInputEmail,
    changeInputPw : changeInputPw,
    handleSubmit : handleSubmit
  }

  return (
    <div>
      <UserContext.Provider value={userData}>
        {
          isLogin == "true" ?
          <DashBoard />
          :
          <Login />

        }
      </UserContext.Provider>
    </div>
  );
}

export default App;
