import React, { useState , useContext, useEffect } from 'react';
import '../css/DashBoard.css';
import { UserContext } from "../App.js";

function DashBoard() {

  const globalState = useContext(UserContext);

  const [withdrawMembers, setWithdrawMembers] = useState(null);
  const [deleteBoards, setDeleteBoards] = useState(null);
  const [loading, setLoading] = useState(true);

  // 탈퇴한 회원 목록 조회용 비동기 요청 함수
  const getWithdrawnMemberList = () => {
    fetch('/admin/withdrawnMemberList')
    .then(response => response.json())
    .then(data => {

      setWithdrawMembers(data);
      setLoading(false); // 로딩 상태 변경
    })
    .catch(error => {
      console.error('Error fetching data:', error);
      setLoading(false); // 로딩 상태 변경
    });
  }

  // 탈퇴한 회원 목록 조회용 비동기 요청 함수
  const restoreMember = (e, member) => {
    console.log(e);
    console.log(member);

    if(window.confirm(member.memberNickname + "님을 탈퇴 복구 시키겠습니까?")) {
      fetch("/admin/restoreMember", {
        method : "PUT",
        headers : {"Content-Type" : "application/json"},
        body : JSON.stringify({ memberNo: member.memberNo })
      })
      .then(res => res.text())
      .then(result => {
        alert("복구 되었습니다");
        getWithdrawnMemberList();
      })
    } else{
      alert("취소");
    }
  }

  

  useEffect(() => {
    getWithdrawnMemberList();
  }, []);

  

  return (
      <div className='dash-board-container'>
        <h1>관리자 페이지</h1>

        <div>
          <p>현재 접속 관리자 : {globalState.user.memberNickname}</p>
          <button onClick={globalState.handleLogout}>로그아웃</button>
        </div>
        
          {
            loading ?
            <div>Loading...</div>
            :
            <div className='menu-box'>
              <section className='section-border'>
                <h2>탈퇴 회원 복구</h2>
                <div>
                  <h3>탈퇴한 회원 목록</h3>

                  {
                    withdrawMembers.length == 0 ? 
                    <p>탈퇴한 회원이 없습니다</p> :
                    withdrawMembers.map((member, index) => {
                      return (
                        <ul key={index}>
                          <li>회원 번호 : {member.memberNo}</li>
                          <li>회원 이메일 : {member.memberEmail}</li>
                          <li>회원 닉네임 : {member.memberNickname}</li>
                          <button onClick={(e) => restoreMember(e, member)}>복구</button>
                        </ul>
                      )
                    })
                  }
                </div>
              </section>
    
              <section className='section-border'>  
                <h2>삭제 게시글 복구</h2>
                <div>
                  <h3>삭제된 게시글 목록</h3>
                </div>
              </section>
            </div>
          }
       

      </div>
  )
}

export default DashBoard;
